let a = 7;
let b = 10;

document.write("El valor de la primera variable es "+ a);

document.write("<p>El valor de la segunda variable es "+ b);

let suma = a + b;
let resta = a - b;
let mult = a * b;
let div = a / b;

document.write("<p>El resultado de la suma de ambos es "+ suma);
document.write("<p>El resultado de la resta de ambos es "+ resta);
document.write("<p>El resultado de la multiplicación de ambos es "+ mult);
document.write("<p>El resultado de la división de ambos es "+ div);